import React from "react";
import explicit from "./explicit.png";
import "./tracks.css";
import { Popover, OverlayTrigger, Button } from "react-bootstrap";
import { FaBars } from "react-icons/fa";
import { useState } from "react";

export default function TrackSearchResult({ track, chooseTrack, spotifyApi }) {
  const [playlistResults, setPlaylistResults] = useState([]);
  function handlePlay() {
    chooseTrack(track);
  }
  function handlePlaylistGet(e) {
    setPlaylistResults(getPlaylists());
  }
  function getPlaylists() {
    if (!playlistResults) return;
    spotifyApi.getUserPlaylists().then((res) => {
      res.body.items.map((playlist) => {
        return {
          name: playlist.name,
          image: playlist.images[2],
        };
      });
    });
    return playlistResults;
  }
  const popover = (
    <Popover id="popover-basic">
      <Popover.Title as="h3">
        <strong>test</strong>
      </Popover.Title>
      <Popover.Content>
        <div>{playlistResults[0].name}</div>
      </Popover.Content>
    </Popover>
  );

  const PlaylistPop = () => (
    <OverlayTrigger trigger="click" placement="left" overlay={popover}>
      <button type="button" className="btn" onClick={handlePlaylistGet}>
        <FaBars />
      </button>
    </OverlayTrigger>
  );
  return (
    <div
      className="d-flex m-2 align-items-center hover-zoom "
      id="tracks"
      style={{ cursor: "pointer" }}
      onClick={handlePlay}
    >
      {" "}
      <div className="m-2">{track.rank}</div>
      <img
        src={track.art}
        className="m-2"
        id="album-art"
        style={{ height: "64px", width: "64px" }}
      />
      <div id="trackDetails" className="m-2">
        <div>
          {track.name}{" "}
          {track.explicit ? (
            <img src={explicit} style={{ height: "16px", width: "16px" }}></img>
          ) : (
            " "
          )}
        </div>
        <div className="text-muted">{track.albumName}</div>
        <div className="text-muted">{"Released: " + track.release}</div>
        {/* <div className="text-muted">{track.artist}</div> */}
      </div>
      <div id="playlist-button">
        {" "}
        <PlaylistPop />
      </div>
    </div>
  );
}
